<div class="table-container">
  <table>
    <thead>
      <tr>
        <th colspan="2">{{ doc.customer_name or "" }}</th>
      </tr>
    </thead>
    <tbody>
      <tr>
        <th>🔍 Status:</th>
        <td>{{ doc_status or "" }}</td>
      </tr>
      <tr>
        <th>🗓 Data da Solicitação:</th>
        <td>{{ doc.transaction_date }}</td>
      </tr>
      <tr>
        <th>🗓 Validade do Orçamento:</th>
        <td>{{ doc.valid_till or "" }}</td>
      </tr>
      <tr>
        <th>🪪 Contato:</th>
        <td>
          {{ doc.contact_display or "" }}
          {% if doc.contact_email %}<br>✉️ {{ doc.contact_email }}{% endif %}
          {% if doc.contact_mobile %}<br>📞 {{ doc.contact_mobile }}{% endif %}
        </td>
      </tr>
      {% if doc.shipping_address.strip() %}
      <tr>
        <th>📬 Endereço de Envio:</th>
        <td>{{ doc.shipping_address.replace("\n", "<br>") }}</td>
      </tr>
      {% endif %}
      {% if doc.address_display.strip() %}
      <tr>
        <th>📮 Endereço de Cobrança:</th>
        <td>{{ doc.address_display.replace("\n", "<br>") }}</td>
      </tr>
      {% endif %}
      <tr>
        <th colspan="2">Downloads</th>
      </tr>
      {% for link in download_links %}
      <tr>
        <th>📎 {{ link.name }}</th>
        <td><a href="{{ link.url }}" target="_alt">⬇️ Baixar</a></td>
      </tr>
      {% endfor %}
    </tbody>
  </table>
</div>
{% else %}
    <p>Você não tem permissão para visualizar esses dados.</p>
{% endif %}
{% endif %}
{% endif %}
