page_data_script:
developer_mode = True
user = 'lucasmatheusc@hotmail.com' if frappe.session.user == 'lucas.campos@eranalitica.com.br' and developer_mode else frappe.session.user

role_profile = frappe.get_doc('User', user).role_profile_name

data.sidebar = [
    {
        "icone": "/files/Orçamento.png",
        "label": "Orçamentos",
        "link": "/portal/orcamentos"
    },
    {
        "icone": "/files/Pedidos.png",
        "label": "Pedidos",
        "link": "/portal/pedidos"
    },
    {
        "icone": "/files/NF.png",
        "label": "Faturamento",
        "link": "/portal/faturas"
    },
    {
        "icone": "/files/Boleto.png",
        "label": "Ordem de Serviço na ER Analítica",
        "link": "/portal/os-er"
    },
    {
        "icone": "/files/Boleto.png",
        "label": "Ordem de Serviço no Cliente",
        "link": "/portal/os-cliente"
    },
    {
        "icone": "/files/Manutenção.png",
        "label": "Equipamentos",
        "link": "/portal/equipamentos"
    },
    {
        "icone": "/files/Lupa.png",
        "label": "Padrões",
        "link": "/portal/padroes"
    }
]

if not role_profile:
    data.sidebar = []

if role_profile == "Cliente - Usuário Portal":
    data.sidebar = [item for item in data.sidebar if item["label"] not in {"Orçamentos", "Pedidos", "Faturamento"}]

if role_profile == "Cliente - Usuário Admin":
    data.sidebar = [item for item in data.sidebar if item["label"] in {"Orçamentos", "Pedidos", "Faturamento", "Ordem de Serviço na ER Analítica", "Ordem de Serviço no Cliente", "Equipamentos", "Padrões"}]


data.os_home = frappe.db.get_list("Ordem Servico Externa", fields=["name", "status_order_service", "equipment_description"], limit_page_length=3)

data.greeting = f"Bem vindo, {frappe.get_doc('User', frappe.session.user).full_name}"

doc = frappe.get_doc("Quotation", frappe.form_dict.docname)
#doc.check_permission()



if not doc:
     frappe.thdoc(_("Você não tem permissão para acessar este documento."), frappe.PermissionError)


if doc.doctype == "Quotation":
    contact_customers = frappe.db.get_all("Dynamic Link", fields=["link_name"], filters={
        "parenttype": "Contact",
        "parent": frappe.db.exists("Contact", {"user": frappe.session.user})
    })

    lista_clientes = [d['link_name'] for d in contact_customers]



history = frappe.db.get_all("Milestone", fields=["*"], filters={
"reference_name": frappe.form_dict.docname,
},
order_by="creation asc")

if doc.get("workflow_state") == "Reprovado":
   doc["status"] = "Reprovado - Orçamento Reprovado"
elif doc.get("status") == "Ordered" or doc.get("workflow_state") == "Aprovado":
   doc["status"] = "Pedido - Seu pedido foi realizado"
elif doc.get("status") == "Open":
   doc["status"] = "Aberto - Aguardando pedido ser realizado"
elif doc.get("status") == "Expired":
    doc["status"] = "Expirado"

# Verifica se transaction_date é uma string
if isinstance(doc.transaction_date, str):
    transaction_date_split = doc.transaction_date.replace("-", "/")
    formatar_datas = transaction_date_split.split("/")  # Agora temos [yyyy, mm, dd]
    # Formata a data como 'dd/mm/yyyy', com 2 dígitos para dia e mês
    doc.transaction_date = f"{int(formatar_datas[2]):02d}/{int(formatar_datas[1]):02d}/{formatar_datas[0]}"
else:
    # Converte para string e aplica a formatação
    doc.transaction_date = str(doc.transaction_date)
    transaction_date_split = doc.transaction_date.replace("-", "/")
    formatar_datas = transaction_date_split.split("/")
