page_data_script:
developer_mode = True
user = 'lucasmatheusc@hotmail.com' if frappe.session.user == 'lucas.campos@eranalitica.com.br' and developer_mode else frappe.session.user

role_profile = frappe.get_doc('User', user).role_profile_name
data.sidebar = [
    {
        "icone": "/files/Orçamento.png",
        "label": "Orçamentos",
        "link": "/portal/orcamentos"
    },
    {
        "icone": "/files/Pedidos.png",
        "label": "Pedidos",
        "link": "/portal/pedidos"
    },
    {
        "icone": "/files/NF.png",
        "label": "Faturamento",
        "link": "/portal/faturas"
    },
    {
        "icone": "/files/Boleto.png",
        "label": "Ordem de Serviço na ER Analítica",
        "link": "/portal/os-er"
    },
    {
        "icone": "/files/Boleto.png",
        "label": "Ordem de Serviço no Cliente",
        "link": "/portal/os-cliente"
    },
    {
        "icone": "/files/Manutenção.png",
        "label": "Equipamentos",
        "link": "/portal/equipamentos"
    },
    {
        "icone": "/files/Lupa.png",
        "label": "Padrões",
        "link": "/portal/padroes"
    }
]
if not role_profile:
    data.sidebar = []

if role_profile == "Cliente - Usuário Portal":
    data.sidebar = [item for item in data.sidebar if item["label"] not in {"Orçamentos", "Pedidos", "Faturamento"}]

if role_profile == "Cliente - Usuário Admin":
    data.sidebar = [item for item in data.sidebar if item["label"] in {"Orçamentos", "Pedidos", "Faturamento", "Ordem de Serviço na ER Analítica", "Ordem de Serviço no Cliente", "Equipamentos", "Padrões"}]

data.greeting = f"Bem vindo, {frappe.get_doc('User', frappe.session.user).full_name}"

doc = frappe.get_doc("Equipamentos", frappe.form_dict.docname)
#doc.check_permission()



if not doc:
     frappe.throw(_("Você não tem permissão para acessar este documento."), frappe.PermissionError)


data.doctype_label = "Equipamento"
data.docname_label = '-'.join(doc.name.rsplit('-', 2)[-1-2:]) 
data.last_modified = doc.get('modified').strftime("%d/%m/%Y %H:%M:%S")

data.doc = doc
data.files = frappe.db.get_all('File', filters={'attached_to_name': doc.name}, fields=['file_name','file_url'])

data.lista_os_externa = frappe.db.get_all('Ordem Servico Externa', fields='*', filters={'serie_number': doc.name, 'customer': doc.customer})
data.lista_os_interna = frappe.db.get_all('Ordem Servico Interna', fields='*', filters={'serie_number': doc.name, 'customer': doc.customer})

data.doc_status = doc.status
data.breadcrumbs = [
    {"label": "Portal", "link": "/portal/orcamentos"},
    {"label": data.doctype_label, "link": "/portal/equipamentos"},
    {"label": data.docname_label, "link": None}
]
#data.link_pdf = f"/printview?doctype={doc.doctype}&name={doc.name}&format=Orcamento"
#data.link_proposal = f"/printview?doctype={doc.doctype}&name={doc.name}&format=Proposta Tecnica"
#data.posting_date = frappe.utils.formatdate(doc.modified)

