.table-container {
  margin: 20px auto;
  max-width: 800px;
  font-family: "Segoe UI", Arial, sans-serif;
  font-size: 14px;
  color: #333;
}

/* Card/table base */
.table-container table {
  width: 100%;
  border-collapse: separate;
  border-spacing: 0;
  background: #fff;
  border-radius: 14px;
  overflow: hidden;
  border: 2px solid #e5e7eb; /* borda mais grossa */
  box-shadow: 0 6px 18px rgba(0,0,0,0.08);
  transition: box-shadow 0.3s ease, transform 0.2s ease;
}

.table-container table:hover {
  box-shadow: 0 10px 28px rgba(0,0,0,0.12);
  transform: translateY(-2px);
}

/* Cabeçalho */
.table-container thead th {
  background: #f8f9fa;
  font-weight: 600;
  text-align: left;
  padding: 16px 20px;
  font-size: 15px;
  border-bottom: 2px solid #e9ecef;
}

/* Células */
.table-container tbody th,
.table-container tbody td {
  padding: 14px 20px;
  text-align: left;
  border-bottom: 1px solid #f1f3f5;
  vertical-align: top;
  transition: background 0.2s ease;
}

/* Coluna da esquerda (rótulos) */
.table-container tbody th {
  width: 230px !important;
  font-weight: 500;
  color: #495057;
  background: #fcfcfc;
  border-right: 1px solid #f1f3f5;
}

/* Hover em cada linha */
.table-container tbody tr {
  transition: background 0.2s ease, transform 0.15s ease, box-shadow 0.15s ease;
}

.table-container tbody tr:hover th,
.table-container tbody tr:hover td {
  background: #fafafa;
}

.table-container tbody tr:hover {
  transform: translateY(-2px);
  box-shadow: inset 0 0 0 1px #e5e7eb, 0 3px 8px rgba(0,0,0,0.05);
  border-radius: 6px;
}

/* Última linha */
.table-container tbody tr:last-child th,
.table-container tbody tr:last-child td {
  border-bottom: none;
}

/* Links */
.table-container a {
  color: #0d6efd;
  text-decoration: none;
  font-weight: 500;
}
.table-container a:hover {
  text-decoration: underline;
}

/* Responsividade */
@media (max-width: 768px) {
  .table-container table,
  .table-container tbody,
  .table-container tr,
  .table-container th,
  .table-container td {
    display: block;
    width: 100%;
  }

  .table-container thead {
    display: none;
  }

  .table-container tbody th {
    border-right: none;
    background: #f8f9fa;
    font-weight: 600;
    padding: 12px 16px;
  }

  .table-container tbody td {
    padding: 12px 16px;
    border-bottom: 1px solid #eee;
  }

  .table-container tbody tr {
    margin-bottom: 14px;
    box-shadow: 0 3px 8px rgba(0,0,0,0.08);
    border-radius: 10px;
    overflow: hidden;
    transition: transform 0.2s ease, box-shadow 0.2s ease;
  }

  .table-container tbody tr:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 14px rgba(0,0,0,0.12);
  }
}
</style>
