    			<td>{{ doc.shipping_address.replace("\n", "<br>") }}</td>
    		</tr>
    		{% endif %}

    		{% if doc.address_display.strip() %}
    		<tr>
    			<th>📮 Endereço de Cobrança: </th>
    			<td>{{ doc.address_display.replace("\n", "<br>") }}</td>
    		</tr>
    		{% endif %}
            <tr><th colspan="2">Downloads</th></tr>
            {% set arquivo = frappe.get_all('File', filters={'attached_to_name': doc.name}, fields=['file_url', 'file_name']) %}
