.table-container {
  margin: 20px auto;
  max-width: 900px;
  font-family: "Segoe UI", Arial, sans-serif;
  font-size: 14px;
  color: #333;
}

/* Card/table base */
.table-container table {
  width: 100%;
  border-collapse: separate;
  border-spacing: 0;
  background: #fff;
  border-radius: 14px;
  overflow: hidden;
  border: 2px solid #e5e7eb; /* borda mais grossa */
  box-shadow: 0 6px 18px rgba(0,0,0,0.08);
  transition: box-shadow 0.3s ease, transform 0.2s ease;
}

.table-container table:hover {
  box-shadow: 0 10px 28px rgba(0,0,0,0.12);
  transform: translateY(-2px);
}

/* Cabeçalho */
.table-container thead th {
  background: #f8f9fa;
  font-weight: 600;
  text-align: left;
  padding: 16px 20px;
  font-size: 15px;
  border-bottom: 2px solid #e9ecef;
}

/* Células */
.table-container tbody th,
.table-container tbody td {
  padding: 14px 20px;
  text-align: left;
  border-bottom: 1px solid #f1f3f5;
  vertical-align: top;
  transition: background 0.2s ease;
}

/* Coluna da esquerda (índice) */
.table-container tbody th {
  width: 60px;
  font-weight: 500;
  color: #495057;
  background: #fcfcfc;
  border-right: 1px solid #f1f3f5;
}

/* Hover em cada linha */
.table-container tbody tr {
  transition: background 0.2s ease, transform 0.15s ease, box-shadow 0.15s ease;
}

.table-container tbody tr:hover th,
.table-container tbody tr:hover td {
  background: #fafafa;
}

.table-container tbody tr:hover {
  transform: translateY(-2px);
  box-shadow: inset 0 0 0 1px #e5e7eb, 0 3px 8px rgba(0,0,0,0.05);
  border-radius: 6px;
}

/* Última linha */
.table-container tbody tr:last-child th,
.table-container tbody tr:last-child td {
  border-bottom: none;
}

/* Responsividade */
@media (max-width: 768px) {
  .table-container table,
  .table-container tbody,
  .table-container tr,
  .table-container th,
  .table-container td {
    display: block;
    width: 100%;
  }

  .table-container thead {
    display: none;
  }

  .table-container tbody th {
    border-right: none;
    background: #f8f9fa;
    font-weight: 600;
    padding: 12px 16px;
  }

  .table-container tbody td {
    padding: 12px 16px;
    border-bottom: 1px solid #eee;
  }

  .table-container tbody tr {
    margin-bottom: 14px;
    box-shadow: 0 3px 8px rgba(0,0,0,0.08);
    border-radius: 10px;
    overflow: hidden;
    transition: transform 0.2s ease, box-shadow 0.2s ease;
  }

  .table-container tbody tr:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 14px rgba(0,0,0,0.12);
  }
}
</style>
{% set role_profile = frappe.get_doc('User', frappe.session.user).role_profile_name %}
{% set user = frappe.get_doc('User', frappe.session.user).email %}

{# Só continua se o perfil for Cliente - Usuário Admin #}
{% if role_profile == "Cliente - Usuário Admin" %}

  {# pega o contato do usuário logado #}
  {% set contacts = frappe.db.get_all("Contact", filters={"user": frappe.session.user}, fields=["name","custom_cliente_restrito"]) %}
  {% if contacts %}
    {% set contact = contacts[0] %}
    {% set is_restricted = contact.custom_cliente_restrito == 1 %}

    {# pega todos os clientes vinculados a esse contato pela Dynamic Link #}
    {% set customers = frappe.db.get_all(
        "Dynamic Link",
        filters={"parenttype": "Contact", "parent": contact.name, "link_doctype": "Customer"},
        fields=["link_name"]
    ) %}
    {% set allowed_customers = customers | map(attribute="link_name") | list %}

    {# Verifica se o usuário tem permissão para ver esse Orçamento #}
    {% if (is_restricted and (doc.customer in allowed_customers and doc.contact_email == user)) 
       or (not is_restricted and doc.customer in allowed_customers) %}
<div class="table-container">
  <table>
    <thead>
      <tr>
        <th>&nbsp;</th>
        <th>Nome</th>
        <th>Descrição</th>
        <th>Qtd</th>
      </tr>
    </thead>
    <tbody>
      {% for row in doc["items"] %}
      <tr>
        <th>{{ row.idx }}</th>
        <td>{{ row.item_name }}</td>
        <td>{{ row.description }}</td>
        <td>{{ row.qty }}</td>
      </tr>
      {% endfor %}
    </tbody>
  </table>    
</div>

<div class="table-container">
  <table>
    <thead>
      <tr>
        <th>Data de Vencimento</th>
        <th>Parcela</th>
        <th>Valor da Parcela</th>
        <th>Valor Pago</th>
      </tr>
    </thead>
    <tbody>
    {% for row in doc["payment_schedule"] %} 
    <tr>
      <td>{{ frappe.utils.get_datetime(row.due_date).strftime("%d/%m/%Y") }}</td> 
      <td>Parcela {{ row.idx }}/{{ loop.length }}</td> 
      <td>R$ {{ '{:,.2f}'.format(row.payment_amount).replace(',', '#').replace('.', ',').replace('#', '.') }}</td>
      <td>R$ {{ '{:,.2f}'.format(row.paid_amount).replace(',', '#').replace('.', ',').replace('#', '.') }}</td> 
    </tr>
    {% endfor %}
    </tbody>
  </table>    
</div>
{% endif %}
{% endif %}
{% endif %}
