page_data_script:
developer_mode = True
user = 'lucasmatheusc@hotmail.com' if frappe.session.user == 'lucas.campos@eranalitica.com.br' and developer_mode else frappe.session.user

role_profile = frappe.get_doc('User', user).role_profile_name
data.sidebar = [
    {
        "icone": "/files/Orçamento.png",
        "label": "Orçamentos",
        "link": "/portal/orcamentos"
    },
    {
        "icone": "/files/Pedidos.png",
        "label": "Pedidos",
        "link": "/portal/pedidos"
    },
    {
        "icone": "/files/NF.png",
        "label": "Faturamento",
        "link": "/portal/faturas"
    },
    {
        "icone": "/files/Boleto.png",
        "label": "Ordem de Serviço na ER Analítica",
        "link": "/portal/os-er"
    },
    {
        "icone": "/files/Boleto.png",
        "label": "Ordem de Serviço no Cliente",
        "link": "/portal/os-cliente"
    },
    {
        "icone": "/files/Manutenção.png",
        "label": "Equipamentos",
        "link": "/portal/equipamentos"
    },
    {
        "icone": "/files/Lupa.png",
        "label": "Padrões",
        "link": "/portal/padroes"
    }
]
if not role_profile:
    data.sidebar = []

if role_profile == "Cliente - Usuário Portal":
    data.sidebar = [item for item in data.sidebar if item["label"] not in {"Orçamentos", "Pedidos", "Faturamento"}]

if role_profile == "Cliente - Usuário Admin":
    data.sidebar = [item for item in data.sidebar if item["label"] in {"Orçamentos", "Pedidos", "Faturamento", "Ordem de Serviço na ER Analítica", "Ordem de Serviço no Cliente", "Equipamentos", "Padrões"}]

data.greeting = f"Bem vindo, {frappe.get_doc('User', frappe.session.user).full_name}"



doc = frappe.get_doc("Sales Invoice", frappe.form_dict.docname)
#doc.check_permission()



if not doc:
     frappe.thdoc(_("Você não tem permissão para acessar este documento."), frappe.PermissionError)

if doc.doctype == "Sales Invoice":
    contact_customers = frappe.db.get_all("Dynamic Link", fields=["link_name"], filters={
        "parenttype": "Contact",
        "parent": frappe.db.exists("Contact", {"user": frappe.session.user})
    })

    lista_clientes = [d['link_name'] for d in contact_customers]


history = frappe.db.get_all("Milestone", fields=["*"], filters={
"reference_name": frappe.form_dict.docname,
},
order_by="creation asc")

if doc.get("status") == "Draft":
    doc["status"] = "Em análise"
elif doc.get("status") == "Return":
    doc["status"] = "Devolução"
elif doc.get("status") == "Credit Note Issued":
    doc["status"] = "Nota de Crédito Emitida"
elif doc.get("status") == "Submitted":
    doc["status"] = "Aguardando pagamento"
elif doc.get("status") == "Paid":
    doc["status"] = "Pago"
elif doc.get("status") == "Partly Paid":
    doc["status"] = "Parcialmente pago"
elif doc.get("status") == "Unpaid":
    doc["status"] = "Não pago"
elif doc.get("status") == "Unpaid and Discounted":
    doc["status"] = "Não pago com desconto aplicado"
elif doc.get("status") == "Partly Paid and Discounted":
    doc["status"] = "Parcialmente pago com desconto aplicado"
elif doc.get("status") == "Overdue and Discounted":
    doc["status"] = "Vencido com desconto aplicado"
elif doc.get("status") == "Overdue":
    doc["status"] = "Vencido"
elif doc.get("status") == "Cancelled":
    doc["status"] = "Cancelado"
elif doc.get("status") == "Internal Transfer":
    doc["status"] = "Transferência interna"


# Verifica se posting_date é uma string
if isinstance(doc.posting_date, str):
    posting_date_split = doc.posting_date.replace("-", "/")
    formatar_datas = posting_date_split.split("/")  # Agora temos [yyyy, mm, dd]
    # Formata a data como 'dd/mm/yyyy', com 2 dígitos para dia e mês
    doc.posting_date = f"{int(formatar_datas[2]):02d}/{int(formatar_datas[1]):02d}/{formatar_datas[0]}"
else:
    # Converte para string e aplica a formatação
    doc.posting_date = str(doc.posting_date)
    posting_date_split = doc.posting_date.replace("-", "/")
    formatar_datas = posting_date_split.split("/")
    doc.posting_date = f"{int(formatar_datas[2]):02d}/{int(formatar_datas[1]):02d}/{formatar_datas[0]}"


#data emissão nota fiscal
# Verifica se posting_date é uma string
if(doc.data_de_emissao_nf):
    if isinstance(doc.data_de_emissao_nf, str):
        data_de_emissao_nf_split = doc.data_de_emissao_nf.replace("-", "/")
        formatar_datas = data_de_emissao_nf_split.split("/")  # Agora temos [yyyy, mm, dd]
        # Formata a data como 'dd/mm/yyyy', com 2 dígitos para dia e mês
        doc.data_de_emissao_nf = f"{int(formatar_datas[2]):02d}/{int(formatar_datas[1]):02d}/{formatar_datas[0]}"
    else:
        # Converte para string e aplica a formatação
        doc.data_de_emissao_nf = str(doc.data_de_emissao_nf)
        data_de_emissao_nf_split = doc.data_de_emissao_nf.replace("-", "/")
        formatar_datas = data_de_emissao_nf_split.split("/")
        doc.data_de_emissao_nf = f"{int(formatar_datas[2]):02d}/{int(formatar_datas[1]):02d}/{formatar_datas[0]}"

data.doc = doc


data.doctype_label = "Fatura"
data.docname_label = '-'.join(doc.name.rsplit('-', 2)[-1 if not doc.amended_from else -2:]) 
data.last_modified = doc.get('modified').strftime("%d/%m/%Y %H:%M:%S")


data.doc_status = doc.status
data.history = history
data.breadcrumbs = [
    {"label": "Portal", "link": "/portal/orcamentos"},
    {"label": data.doctype_label, "link": "/portal/faturas"},
    {"label": data.docname_label, "link": None}
]






#data.transaction_date = frappe.utils.formatdate(doc.modified)

#data.item_cols = ["idx", "item_code", "item_name", "description", "qty"]

