    # Pega apenas a parte da data antes do espaço
    data_str = doc.creation.split(" ")[0]  # "2025-08-20"
    data_split = data_str.split("-")       # ["2025", "08", "20"]
    doc.creation = f"{int(data_split[2]):02d}/{int(data_split[1]):02d}/{data_split[0]}"
