    # Converte para string e corta antes do espaço
    data_str = str(doc.creation).split(" ")[0]  # "2025-08-20"
    data_split = data_str.split("-")
    doc.creation = f"{int(data_split[2]):02d}/{int(data_split[1]):02d}/{data_split[0]}"




data.doctype_label = "OS na ER"
