<div class="table-container">
  <table>
    <thead>
      <tr>
        <th colspan="2">Detalhes da Calibração</th>
      </tr>
    </thead>
    <tbody>
      <tr>
        <th>🗓 Data da Calibração:</th>
        <td> 
                {% if doc.cal_rbc == 1 or doc.cal_rastreavel == 1 %}
                    {{ doc.data_cal.strftime('%d/%m/%Y') if doc.data_cal else "OS não possui calibração" }}
                {% else %}
                    OS não possui calibração
                {% endif %}
        </td>
      </tr>
      <tr>
        <th>🗓 Data da Calibração Recomendada:</th>
         <td> 
                {% if doc.cal_rbc == 1 or doc.cal_rastreavel == 1 %}
                    {{ doc.data_cal_recomendada.strftime('%d/%m/%Y') if doc.data_cal_recomendada else "OS não possui calibração" }}
                {% else %}
                    OS não possui calibração
                {% endif %}
        </td>
      </tr>
      <tr>
        <th>📄 Certificado de Calibração:</th>
         <td> 
                {% if doc.cal_rbc == 1 or doc.cal_rastreavel == 1 %}
                    {% if doc.anexo_certificado %}
                        <a href="{{ doc.anexo_certificado }}" target="_blank">📥 Baixar Certificado</a>
                    {% else %}
                        Certificado não disponível
                    {% endif %}
                {% else %}
                    OS não possui calibração
                {% endif %}
        </td>
      </tr>

    </tbody>
  </table>
</div>

<div class="table-container">
  <table>
    <thead>
      <tr>
        <th colspan="2">Anexos da OS</th>
      </tr>
    </thead>
    <tbody>
      <tr>
    		{% set arquivo = frappe.get_all('File', filters={'attached_to_name': doc.name}, fields=['file_url', 'file_name']) %}
            {% for link in arquivo %}
            <tr>
                <th>📎 {{ link.file_name }}</th>
                <td><a href="{{ link.file_url }}" target="_alt">📥 Baixar</a></td>
            </tr>
            {% endfor %}
      </tr>
    </tbody>
  </table>
</div>
{% endif %}
{% endif %}
