script:
document.addEventListener("DOMContentLoaded", function() {
    const searchInput = document.querySelector("input[type=search]");
    const rows = document.querySelectorAll(".table-row");

    if (!searchInput || rows.length === 0) return;

    searchInput.addEventListener("input", function() {
        const searchTerm = this.value.toLowerCase();
        rows.forEach(row => {
            const text = row.textContent.toLowerCase();
            if (text.includes(searchTerm)) {
                row.classList.remove("hidden"); // mostra a div compatível
            } else {
                row.classList.add("hidden"); // esconde a div incompatível
            }
        });
    });
});

style:
::-webkit-scrollbar {
    width: .5rem;
    background-color: var(--surface-gray-1, #F8F8F8)
}

::-webkit-scrollbar-thumb {
    border-radius: 9999px;
    background-color: var(--surface-gray-3, #EDEDED)
}


.sidebar-item.active {
    background-color: rgba(0, 157, 222, 0.5);
}

.text-right {
    align-items: flex-end;
    flex-direction: column;
    padding-right: 15px;
}

.table-row:nth-child(even) {
    background-color: rgba(192, 228, 246, 0.2);
}

.table-row.hidden {
    display: none;
}
