script:
frappe.ui.form.on("Sales Invoice", {
  on_submit: function(frm) {
    console.log("frm.doc.reference_name: " + frm.doc.reference_name);
    // Recupera o valor total da fatura de venda
    var invoice_total = frm.doc.base_total;

    // Recupera o valor total do pedido de venda correspondente
    var orders = frappe.db.get_list("Sales Order", { filters: { reference_name: frm.doc.reference_name } });

    var order_total = 0;
    if (orders.length > 0) {
      order_total = orders[0].base_total;
    }

    console.log("order_total: " + order_total);
    // Compare os valores para ver se eles são iguais
    if (invoice_total === order_total) {
      // Se os valores forem iguais, use a função frappe.set_value para definir o estado do pedido como concluído
      frappe.model.set_value("Sales Order", frm.doc.reference_name, "status", "Closed");
      frappe.model.set_value("Sales Order", frm.doc.reference_name, "per_delivered", 100);
    }
  }
});

frappe.ui.form.on('Customer', {
    onload: function(frm) {
        (async () => {
            const response = await fetch("https://unpkg.com/imask");
            const scriptText = await response.text();
            const script = document.createElement("script");
            script.text = scriptText;
            document.head.appendChild(script);
            const tipoDocumento = frm.fields_dict['cnpj_cpf'].$input[0];
            const numeroDocumento = frm.fields_dict['cnpj'].$input[0];
            let mask = null;
            numeroDocumento.setAttribute("maxlength", 18);
            mask = IMask(numeroDocumento, {
                mask: '00.000.000/0000-00'
            });
            tipoDocumento.addEventListener("change", function() {
                if (mask) {
                    mask.destroy();
                }
                if (this.value === "CPF") {
                    numeroDocumento.value = "";
                    numeroDocumento.setAttribute("maxlength", 14);
                    mask = IMask(numeroDocumento, {
                        mask: '000.000.000-00'
                    });
                } else if (this.value === "CNPJ") {
                    numeroDocumento.value = "";
                    numeroDocumento.setAttribute("maxlength", 18);
                    mask = IMask(numeroDocumento, {
                        mask: '00.000.000/0000-00'
                    });
                }
            });
        })();
    }
});

frappe.ui.form.on("Customer", "cnpj", function(frm) {
    frappe.call({
        method: 'ordem_servico.api.get_cnpj',
        args: {
            'cnpj': frm.doc.cnpj,
        },
        callback: function(r) {
            // code snippet
            debugger;
            Object.keys(r.message).forEach(key => frm.set_value(key, r.message[key]));
        }
    });
});

frappe.ui.form.on("Quotation", {
    validate(frm){
	if (frm.doc.defeito_constatado && frm.doc.defeito_constatado.length){
         frm.set_value("defeito_constatado", frm.doc.defeito_constatado.replaceAll("img src=", 'img style="max-width:150px; max-height:150px; width: auto; height: auto;" src='));
    }
}
});

frappe.ui.form.on("Address", "cep", function (frm) { 
    if (!frm.doc.cep) 
    return; 
frappe.call({ 
    method: 'ordem_servico.api.get_cep',
    args: {
	   'cep': frm.doc.cep,
    	}, 
    callback: function(r) {
        // code snippet
	debugger;
	Object.keys(r.message).forEach(key => frm.set_value(key, r.message[key]));
    } 
    }); 
});

frappe.ui.form.on("Opportunity", {
  refresh: function(frm) {
    setTimeout(function() {
      // código para esconder elementos na página
      $('div.open-events.pl-1').hide();
      $('#opportunity-activities_tab > div:nth-child(1) > div > div > form > div > div > div.new-btn.pb-3 > span > button.btn.btn-sm.small.new-event-btn').hide();
      $('#opportunity-activities_tab > div:nth-child(1) > div > div > form > div.frappe-control.form-group > div').hide();
      $(document).ajaxComplete(function() {
        // código para esconder elementos na página
        $('div.open-events.pl-1').hide();
        $('#opportunity-activities_tab > div:nth-child(1) > div > div > form > div > div > div.new-btn.pb-3 > span > button.btn.btn-sm.small.new-event-btn').hide();
        $('#opportunity-activities_tab > div:nth-child(1) > div > div > form > div.frappe-control.form-group > div').hide();
      });
    },50);
  }
});

frappe.realtime.on("doc_update", data => {
    if (data.doctype == cur_frm.doctype && data.name == cur_frm.docname) {
        cur_frm.reload_doc();
        console.log("Recarregando....")
    }
});
