frappe.ui.form.on("Customer", {
    validate: function(frm) {

      var valida = '';

      // Garante que o valor é uma string
      valor = frm.doc.tax_id.toString();

      // Remove caracteres inválidos do valor
      valor = valor.replace(/[^0-9]/g, '');

      // Verifica CPF
      if ( valor.length === 11 ) {
         valida = 'CPF';
      } 

      // Verifica CNPJ
      else if ( valor.length === 14 ) {
         valida = 'CNPJ';
      } 

      if ( valida === 'CPF' ) {
         var strCpf = valor;
         var soma;
         var resto;
         soma = 0;
         if (strCpf == "00000000000" ||
             strCpf == "11111111111")
                       {
                       msgprint(strCpf, "CPF invalido");
                       validated = false;
                       }
         else {
         for (i = 1; i <= 9; i++) {
             soma = soma + parseInt(strCpf.substring(i - 1, i)) * (11 - i);
             }
         resto = soma % 11;

         if (resto == 10 || resto == 11 || resto < 2) {
             resto = 0;
         } else {
             resto = 11 - resto;
         }
         if (resto != parseInt(strCpf.substring(9, 10))) {
         msgprint("Você informou um cpf inválido - mensagem 2");
         validated = false;
                 }
         soma = 0;
         for (i = 1; i <= 10; i++) {
             soma = soma + parseInt(strCpf.substring(i - 1, i)) * (12 - i);
         }
         resto = soma % 11;
         if (resto == 10 || resto == 11 || resto < 2) {
             resto = 0;
         } else {
             resto = 11 - resto;
                      }
         if (resto != parseInt(strCpf.substring(10, 11))) {
             msgprint(strCpf, "CPF Invalido");
             validated = false;
         }else{
             validated = true;
             strCpf =strCpf.substring(0,3).concat(".",strCpf.substring(3,6),".",strCpf.substring(6,9),"-",strCpf.substring(9,11));
             frm.doc.tax_id = strCpf;
             frm.doc.customer_type = "Individual";
         }
      }
      }
      else if ( valida === 'CNPJ' ) {
             var cnpj = valor;
             var valida = new Array(6,5,4,3,2,9,8,7,6,5,4,3,2);
             var dig1= new Number;
             var dig2= new Number;
             exp = /\.|\-|\//g
             cnpj = cnpj.toString().replace( exp, "" ); 
             var digito = new Number(eval(cnpj.charAt(12)+cnpj.charAt(13)));

             for(i = 0; i<valida.length; i++){
                dig1 += (i>0? (cnpj.charAt(i-1)*valida[i]):0);  
                dig2 += cnpj.charAt(i)*valida[i];       
             }
             dig1 = (((dig1%11)<2)? 0:(11-(dig1%11)));
             dig2 = (((dig2%11)<2)? 0:(11-(dig2%11)));

             if(((dig1*10)+dig2) != digito){
                msgprint(cnpj, "CNPJ Invalido");
                validated = false; 
             }else{
                validated = true;
//                cnpj =cnpj.substring(0,2).concat(".",cnpj.substring(2,5),".",cnpj.substring(5,8),"/",cnpj.substring(8,12),"-",cnpj.substring(12,14));
             frm.doc.tax_id = cnpj;
             frm.doc.customer_type = "Company";
             }
      }
       else{
          msgprint(valor, "CPF ou CNPJ Invalido");
          validated = false;
      }
    }
});
