frappe.ui.form.on("Purchase Invoice", "before_save", function(frm) {
	// Para cada linha de impostos
	$.each(frm.doc.taxes || [], function(i, d) {
      		// Atualiza aliquota
      		if(d.aliquota.length == 0) { 
			d.aliquota = d.rate;
		}
      		// Se aliquota calculada diferente de rate, atualiza rate
      		var_aliquota = d.aliquota * d.reducao_base / 100;
      		if( var_aliquota != d.rate) {
			d.rate = var_aliquota
		};
	});
});
