context_script:
customer_by_doctype = {
    "Quotation": {
        "customer": "party_name",
        "filters": {
            "docstatus": ["in", [1, 2]],
            "workflow_state": ["!=", "Cancelado"]
        }
    },
    "Sales Order": {
    	"customer": "customer",
    	"filters": {
    		"docstatus": 1
    	}
    },
    "Sales Invoice": {
    	"customer": "customer",
    	"filters": {
    		"docstatus": 1
    	}
    },
    "Ordem Servico Interna": {
    	"customer": "customer",
    	"filters": {}
    },
    "Ordem Servico Externa": {
    	"customer": "customer",
    	"filters": {
    	    "status_order_service": "Encerrada",
    	}
    },
    "Equipamentos": {
    	"customer": "customer",
    	"filters": {}
    }
}

context.contact_customers = frappe.get_all("Dynamic Link", fields=["link_name"], filters={
    "parenttype": "Contact",
    "parent": frappe.db.exists("Contact", {"user": frappe.session.user})
})

context.rows = frappe.get_list(frappe.form_dict.doctype, fields="*", filters={
     customer_by_doctype[frappe.form_dict.doctype]["customer"]: ["in", [cust.link_name for cust in context.contact_customers]], 
    ** customer_by_doctype[frappe.form_dict.doctype]["filters"]
})
if frappe.form_dict.doctype == "Quotation":
    for row in context.rows:
        if row.get("workflow_state") == "Reprovado":
             row["status"] = "Reprovado - Orçamento Reprovado"
        elif row.get("status") == "Ordered" or row.get("workflow_state") == "Aprovado":
             row["status"] = "Pedido - Seu pedido foi realizado"
        elif row.get("status") == "Open":
             row["status"] = "Aberto - Aguardando pedido ser realizado"


context.fields_by_doctype = {
    "Quotation": ["name", "status","net_total"], 
    "Sales Order": ["name","status", "net_total"],
    "Sales Invoice": ["name","status","numero_da_nf", "net_total"],
    "Ordem Servico Interna": ["name", "status_order_service", "equipment_description"],
    "Ordem Servico Externa": ["name", "status_order_service", "repair_status", "equipment_description"],
    "Equipamentos": ["name", "modelo_equipamento", "tag"]
}

