{% set fields = fields_by_doctype.get(frappe.form_dict.doctype, [] ) %}

{% set meta = frappe.get_meta(frappe.form_dict.doctype) %}
<table class="table table-condensed table-stripped">
    <thead>
        {% for field in fields %}
            <th>{{ _(meta.get_label(field)) }}</th>
        {% endfor %}
        <th>{{ _("Actions") }}</th>
    </thead>
    <tbody>
      {% for row in rows %}
          <tr>
             {% for field in fields %}
             <td>{{ _(row.get(field)) or "" }}</td>
             {% endfor %}
             <td><a href="/ver/{{ frappe.form_dict.doctype }}/{{ row.name }}" target="_blank">{{ _("View") }}</a></td>
         </tr>
      {% endfor %}
    </tbody>
</table>

