css:
/* =========================
   TAMANHO DA PÁGINA (ETIQUETA)
   ========================= */
@page {
      /* TAMANHO FÍSICO DA ETIQUETA */
    margin: 0;         /* SEM MARGEM DA IMPRESSORA */
}

/* =========================
   RESET GERAL (NÃO MEXA)
   ========================= */
*, *::before, *::after {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

/* =========================
   BLINDAGEM CONTRA BORDAS
   (ESSENCIAL PARA wkhtmltopdf)
   ========================= */
div, table, tr, td {
    border: none !important;
    outline: none !important;
    box-shadow: none !important;
}

/* =========================
   BASE DO DOCUMENTO
   ========================= */
html, body {
    width: 40mm;

    font-family: Arial, sans-serif;
}

/* ==================================================
   ÁREA PRINCIPAL DA ETIQUETA
   🔧 AQUI VOCÊ MAIS VAI MEXER
   ================================================== */
.label {
    width: 40mm;
    height: 40mm;


    /* 📍 POSICIONAMENTO GERAL */
    display: flex;
    flex-direction: column;

    justify-content: flex-start;  /* vertical:
                                     flex-start = topo
                                     center = centro
                                     flex-end = fundo */

    align-items: flex-start;      /* horizontal:
                                     flex-start = esquerda
                                     center = centro
                                     flex-end = direita */

    text-align: left;             /* alinhamento do texto */

}

/* ==================================================
   HERANÇA DE FONTE (NÃO REMOVER)
   ================================================== */
.label * {

    line-height: 1.1;     /* ↕ espaçamento entre linhas */
}

/* ==================================================
   CONTROLE ENTRE BLOCOS (DATA / SÉRIE / CÓDIGO)
   ================================================== */
.label > * + * {
    margin-top: 2mm;     /* ↕ espaço ENTRE os textos */
}

/* =========================
   AJUSTES INDIVIDUAIS (OPCIONAL)
   ========================= */
.label .data {
    /* margin-top: 0mm; */
}

.label .serie {
    /* margin-top: 0mm; */
}

.label .codigo {
    /* margin-top: 0mm; */
}

html:
{# =========================
   CONTROLE DO SERIE_NUMBER
   ========================= #}
{% if doc.custom_identificacao_dos_equipamentos == "Número de série" %}
{% set tam_serie = doc.serie_number and (doc.serie_number|string)|length or 0 %}
{% else %}
{% set tam_serie = doc.equipment_tag and (doc.equipment_tag|string)|length or 0 %}
{% endif %}

{% if tam_serie <= 9 %}
    {% set tamanho_fonte = "3mm" %}
    {% set espaco_personalizado = "0 0 16px 7px;" %}

{% elif tam_serie >= 10 and tam_serie <= 12 %}
    {% set tamanho_fonte = "2.5mm" %}
    {% set espaco_personalizado = "2px 0 18px 3px;" %}

{% elif tam_serie >= 13 and tam_serie <= 15 %}
    {% set tamanho_fonte = "2.5mm" %}
    {% set espaco_personalizado = "4px 0 15px -13px;" %}

{% elif tam_serie >= 16 and tam_serie <= 20 %}
    {% set tamanho_fonte = "2mm" %}
    {% set espaco_personalizado = "4px 0 17px -15px" %}
{% endif %}

{# =========================
   QUANTIDADE DE ETIQUETAS
   (REMOVE "_" E CONVERTE)
   ========================= #}
{% set qtd = (doc.custom_quantidade_certificados | replace("_", "")) | int %}

{# =========================
   BASE DA OS (REMOVE OS-)
   ========================= #}
{% set base_os = (doc.name | string) | replace("OS-", "") %}

<style>
    /* 🔠 TAMANHO DA FONTE CONTROLADO PELO TAMANHO DO SERIE_NUMBER */
    .label {
        padding-top: 4mm;
        padding-left: 16mm;
        padding-right: 0mm;
        padding-bottom: 0mm;
    }
</style>

{% for i in range(1, qtd + 1) %}

{# =========================
   SUFIXO _01, _02, _03...
   ========================= #}
{% set sufixo = "%02d"|format(i) %}

{# =========================
   CODIGO FINAL
   EX: 020314_03
   ========================= #}
{% set codigo_final = "0" ~ base_os ~ "_" ~ sufixo %}

<div class="label">
    <br>
    <br>

    <p style="margin: 5px 0 17px 0; font-size: 3mm;">
        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
        <strong>{{ frappe.utils.get_datetime(doc.data_cal).strftime("%d/%m/%Y") or "" }}</strong>
    </p>

    <p style="margin: {{ espaco_personalizado }};">
        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
        <strong style="font-size: {{ tamanho_fonte }};">
            {% if doc.custom_identificacao_dos_equipamentos == "Número de série" %}
            {{ doc.serie_number or "" }}
            {% else %}
            {{ doc.equipment_tag or "" }}
            {% endif %}
        </strong>
    </p>

    <p style="margin: -2px 0 0 0; font-size: 3mm;">
        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
        <strong>{{ codigo_final }}</strong>
    </p>
</div>

<div style="page-break-after: always;"></div>

{% endfor %}
