css:
/* esconde o número de página quando ele é renderizado como elemento HTML com essa classe
   (ERPNext costuma usar .page-number) */
.page-number {
  display: none !important;
}

/* outros seletores comuns de rodapé/header embutidos */
.footer, footer, .pdf-footer, .page-footer {
  display: none !important;
}

/* garantir que só vale para impressão/PDF */
@media print {
  .page-number, .footer, footer, .pdf-footer, .page-footer {
    display: none !important;
  }
}
